import HolbertonLogo from './HolbertonLogo.jpg';
import './App.css';
import { getFullYear, getFooterCopy } from './utils';
import { Notifications } from './Notifications';

function App() {
  return (
    <div className="App">
      <div className='root-notifications'>
        {Notifications()}
      </div>
      <div className="App-header">
        <img className='App-logo' src={HolbertonLogo} alt='logo'></img>
        <h1>School dashboard</h1>
      </div>

      <div className='App-body'>
        <p>Login to access the full dashboard</p>
        <div>
          <label className="labe"for="email">Enter your email:</label>
          <input type="email" id="email" name="email"></input>
          <label for="password" >Enter your password:</label>
          <input type="password" id="pass" name="password"
            minlength="8" required></input>
          <input type="submit" value="OK"></input>
        </div>
      </div>
      <div className='App-footer'>
        <p>
          {getFullYear()} {getFooterCopy()}
        </p>
      </div>

    </div>


  );
}

export default App;
